
CREATE TABLE `vmei_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `vmei_id` int(11) NOT NULL DEFAULT '0',
  `uniacid` int(11) DEFAULT '0',
  `name` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `thumb` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `parent_id` int(11) DEFAULT '0',
  `description` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `display_order` int(11) DEFAULT '0',
  `enabled` tinyint(1) DEFAULT '1',
  `is_home` tinyint(1) DEFAULT '0',
  `adv_img` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT '',
  `adv_url` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT '',
  `level` tinyint(1) DEFAULT '0',
  `advimg_pc` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `advurl_pc` varchar(500) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `created_at` int(11) DEFAULT NULL,
  `updated_at` int(11) DEFAULT NULL,
  `deleted_at` int(11) DEFAULT NULL,
  `plugin_id` int(11) NOT NULL DEFAULT '0',
  `filter_ids` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci ROW_FORMAT=DYNAMIC;






CREATE TABLE `vmei_goods` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `vmei_id` int(11) unsigned NOT NULL DEFAULT '0',
  `name` varchar(100) NOT NULL DEFAULT '',
  `thumb` varchar(255) NOT NULL DEFAULT '',
  `sell` int(8) unsigned NOT NULL DEFAULT '0',
  `size` varchar(300) NOT NULL DEFAULT '',
  `discount` varchar(30) NOT NULL DEFAULT '',
  `attribute` text NOT NULL,
  `details` text NOT NULL,
  `description` text NOT NULL,
  `price` float(8,2) unsigned NOT NULL DEFAULT '0.00',
  `marketPrice` float(8,2) unsigned NOT NULL DEFAULT '0.00',
  `primaryPrice` float(8,2) unsigned NOT NULL DEFAULT '0.00',
  `brand_name` varchar(40) NOT NULL DEFAULT '',
  `first_category_id` int(5) unsigned NOT NULL DEFAULT '0',
  `second_category_id` int(5) unsigned NOT NULL DEFAULT '0',
  `vmei_category_id` int(5) unsigned NOT NULL DEFAULT '0',
  `goods_id` int(11) unsigned NOT NULL DEFAULT '0',
  `group_id` int(11) unsigned NOT NULL DEFAULT '0',
  `store_id` int(11) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `sell` (`sell`) USING BTREE,
  KEY `name` (`name`) USING BTREE,
  KEY `vmei_id` (`vmei_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;







